import Card from "../UI/Card";
import MealItem from "./MealItem/MealItem";
import classes from "./AvailableMeals.module.css";
import { useState } from "react";

const AvailableMeals = () => {
  const [mealData, setMealData] = useState([]);
  const [error, SetError] = useState(null);

  const fetchHandler = async () => {
    try {
      const response = await fetch(
        "https://mealproject-a3b4a-default-rtdb.europe-west1.firebasedatabase.app/"
      );
      if (!response.ok) {
        throw new Error("something went wrong");
      }
      const data = await response.json();
      const uptadetedMeals = data.meals.map((meal) => {
        return {
          id: meal.id,
          name: meal.name,
          description: meal.description,
          price: meal.price,
        };
      });
      setMealData(uptadetedMeals);
    } catch (err) {
      SetError(error.message);
    }
  };

  const mealsList = mealData.map((meal) => (
    <MealItem
      key={meal.id}
      id={meal.id}
      name={meal.name}
      description={meal.description}
      price={meal.price}
    />
  ));

  return (
    <section className={classes.meals}>
      <Card>
        <ul>{mealsList}</ul>
        <button onClick={fetchHandler}>GETÄ°R</button>
      </Card>
    </section>
  );
};

export default AvailableMeals;
